mapper.select:
SELECT NOVEL_ID , TITLE , GENRE , DESCRIPTION , PUBLICATION_DATE , PUBLICATION_STATUS , CREATED_AT FROM NOVEL WHERE 1 = 1
AND TITLE = #{title}
AND AU = #{title}

mapper.insert:
INSERT INTO NOVEL
(
TITLE
, GENRE
, DESCRIPTION
, PUBLICATION_DATE
, PUBLICATION_STATUS
, CREATED_AT
) VALUES
      ( #{title}
      , #{genre}
      , #{description}
      , #{publicationDate}
      , #{publicationStatus}
      , #{createdAt} )

mapper.update:
UPDATE NOVEL SET
            TITLE = #{title}
          , GENRE = #{genre}
          , DESCRIPTION = #{description}
          , PUBLICATION_STATUS = #{publicationStatus}
          , PUBLICATION_DATE = #{publicationDate}
WHERE NOVEL_ID = #{novelId}
